<?phpnamespace Akmalmp\BelajarPhpMvc\Config;use PHPUnit\Framework\TestCase;class DatabaseTest extends TestCase{    public function testGetConnection()    {        $connection = Database::getConnection();        var_dump($connection);        self::assertNotNull($connection);    }    public function testConnectionSingleTon()    {        $connection1 = Database::getConnection();        $connection2 = Database::getConnection();        self::assertSame($connection1, $connection2);    }}